import React, {useState, Fragment} from 'react';

function Formulario(){
  const [cita, actualizarCita ] = useState({
    mascota:'',
    propietario:'',
    fecha:'',
    hora:'',
    sintomas:''
  });

  const handleChange= e =>{
    actualizarCita({
      ...cita,
      [e.target.name] : e.target.value

    })

  }

  //mandar el form
  const handleSubmit= e =>{
    e.preventDefault();

    // pasar la cita al componente principial
    console.log(cita);
  }


  return (
    <Fragment>
      <h2>Crear Cita</h2>

      <form
      onSubmit={handleSubmit}
      >
                  <label>Nombre Mascota</label>
                  <input 
                    type="text" 
                    name="mascota"
                    className="u-full-width" 
                    placeholder="Nombre Mascota"
                    onChange={handleChange} 
                  />

                  <label>Nombre Due√±o</label>
                  <input 
                    type="text" 
                    name="propietario"
                    className="u-full-width"  
                    placeholder="Nombre Due√±o de la Mascota" 
                    onChange={handleChange} 
                  />

                  <label>Fecha</label>
                  <input 
                    type="date" 
                    className="u-full-width"
                    name="fecha"
                    onChange={handleChange} 
                  />               

                  <label>Hora</label>
                  <input 
                    type="time" 
                    className="u-full-width"
                    name="hora" 
                    onChange={handleChange} 
                  />

                  <label>Sintomas</label>
                  <textarea 
                    className="u-full-width"
                    name="sintomas"
                    onChange={handleChange} 
                  ></textarea>

                  <button type="submit" className="button-primary u-full-width">Agregar</button>
          </form>
  </Fragment>
  )
}

function App() {
  //hook todo en un archivo
  const [citas, guardarCitas] = useState([]);

  const creaCita = cliente =>{
    const nuevaCita = [...cita, cliente];
    console.log(nuevaCita);
  }



  return (
    <Fragment>
      <h1>Administardor de pacientes</h1>
      <div className="container">
        <div className="row">
          <div className="one-half column">
              <Formulario/>
          </div>
          <div className="one-half column">

          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default App;
